CREATE TABLE `truyen` (
  `id` int(11) NOT NULL,
  `hinh` varchar(255) COLLATE utf8mb4_unicode_ci NOT NULL,
  `ten` varchar(150) COLLATE utf8mb4_unicode_ci NOT NULL,
  `ten_khongdau` varchar(160) COLLATE utf8mb4_unicode_ci NOT NULL,
  `chuong` int(11) NOT NULL DEFAULT 0,
  `tacgia` varchar(50) COLLATE utf8mb4_unicode_ci NOT NULL,
  `nguon` varchar(255) COLLATE utf8mb4_unicode_ci NOT NULL,
  `trangthai` varchar(50) COLLATE utf8mb4_unicode_ci NOT NULL DEFAULT 'Đang cập nhật',
  `gioithieu` text COLLATE utf8mb4_unicode_ci DEFAULT NULL,
  `danhgia` float NOT NULL DEFAULT 0,
  `ngaytao` timestamp NOT NULL DEFAULT current_timestamp(),
  `capnhat` timestamp NOT NULL DEFAULT current_timestamp() ON UPDATE current_timestamp()
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;

--
-- Đang đổ dữ liệu cho bảng `truyen`
--

INSERT INTO `truyen` (`id`, `hinh`, `ten`, `ten_khongdau`, `chuong`, `tacgia`, `nguon`, `trangthai`, `gioithieu`, `danhgia`, `ngaytao`, `capnhat`) VALUES
(9, 'meo-day-hai-au-bay-luis-sepulveda.jpg', 'Mèo Dạy Hải Âu Bay', 'meo-day-hai-au-bay', 3, 'Luis Sepulveda', 'Thuviensach.com', 'Đang cập nhật', '<p><span style=\"color:rgb(8, 28, 54); font-family:segoeuipc,segoe ui,san francisco,helvetica neue,helvetica,lucida grande,roboto,ubuntu,tahoma,microsoft sans serif,arial,sans-serif; font-size:15px\">C&oacute; tồn tại kh&ocirc;ng t&igrave;nh thương y&ecirc;u giữa m&egrave;o v&agrave; hải &acirc;u? Thế giới n&agrave;y đầy những nghịch l&yacute; v&agrave; kh&aacute;c biệt, nhưng bỏ qua những kh&aacute;c biệt đ&oacute; để hướng đến t&igrave;nh y&ecirc;u thương th&igrave; cuộc sống sẽ tốt đẹp hơn. <em><strong>&ldquo;Chuyện con m&egrave;o dạy hải &acirc;u bay&rdquo;</strong></em> của nh&agrave; văn Chi L&ecirc; nổi tiếng Luis S&eacute;Pulveda l&agrave; một c&acirc;u chuyện thấm đẫm t&igrave;nh m&egrave;o, t&igrave;nh người như thế. C&acirc;u chuyện l&agrave; cuộc h&agrave;nh tr&igrave;nh d&agrave;i đi thực hiện ba lời hứa của ch&uacute; m&egrave;o mập Zorba: &ldquo;sẽ kh&ocirc;ng ăn quả trứng&rdquo;, sẽ &ldquo;chăm lo cho quả trứng đến khi ch&uacute; chim non ra đời&rdquo;, v&agrave; điều cuối dường như kh&ocirc;ng tưởng l&agrave; &ldquo;dạy n&oacute; bay&rdquo;. Những rắc rối li&ecirc;n tiếp ập đến, liệu một b&agrave; m&aacute; rất &ldquo;xịn&rdquo; như Zorba c&oacute; thực hiện đ&uacute;ng được ba lời hứa? T&igrave;nh thương gi&uacute;p thay đổi định kiến. Mọi kh&oacute; khăn đ&atilde; chứng minh rằng sau thẳm b&ecirc;n trong ch&uacute; m&egrave;o Zorba l&agrave; một tr&aacute;i tim nh&acirc;n hậu, tr&agrave;n trề thứ t&igrave;nh cảm gọi l&agrave; &ldquo;y&ecirc;u thương ch&acirc;n th&agrave;nh&rdquo;. Ch&iacute;nh thứ t&igrave;nh cảm n&agrave;y đ&atilde; k&eacute;o c&ocirc; b&eacute; chim hải &acirc;u nhỏ gần lại với m&egrave;o Zorba, bởi &ldquo;Thật dễ d&agrave;ng để chấp nhận v&agrave; y&ecirc;u thương một kẻ n&agrave;o đ&oacute; giống m&igrave;nh, nhưng để y&ecirc;u thương ai đ&oacute; kh&aacute;c m&igrave;nh thực sự rất kh&oacute; khăn..&rdquo;. Vậy đấy, y&ecirc;u thương l&agrave; học c&aacute;ch chấp nhận sự kh&aacute;c biệt v&agrave; kh&ocirc;ng c&oacute; &yacute; định muốn biến người đ&oacute; trở n&ecirc;n giống m&igrave;nh. Khi ch&uacute;ng ta y&ecirc;u thương ai đ&oacute; bằng tất cả sự ch&acirc;n th&agrave;nh, th&igrave; mọi định kiến v&agrave; kh&aacute;c biệt chỉ l&agrave; điểm tựa cho t&igrave;nh cảm cao đẹp trong lo&agrave;i m&egrave;o, lo&agrave;i người ấy được s&acirc;u sắc hơn th&ocirc;i.</span></p>\r\n', 0, '2023-11-29 14:39:14', '2023-11-29 14:45:49'),
(10, 'tam-cam-truyen-co-tich-viet-nam.jpg', 'Tấm Cám', 'tam-cam', 1, 'Truyện Cổ Tích Việt Nam', 'Thuviensach.com', 'Hoàn thành', '<p>&nbsp;</p>\r\n\r\n<p><span style=\"color:rgb(8, 28, 54); font-family:segoeuipc,segoe ui,san francisco,helvetica neue,helvetica,lucida grande,roboto,ubuntu,tahoma,microsoft sans serif,arial,sans-serif; font-size:15px\">Ng&agrave;y xửa ng&agrave;y xưa, c&oacute; hai chị em c&ugrave;ng cha kh&aacute;c mẹ t&ecirc;n l&agrave; Tấm v&agrave; C&aacute;m. Mẹ Tấm mất sớm, c&ograve;n cha Tấm cưới th&ecirc;m mẹ C&aacute;m, cha Tấm v&ocirc; c&ugrave;ng hết mực y&ecirc;u thương c&ocirc;, nhưng rồi &ocirc;ng bệnh nặng, kh&ocirc;ng l&acirc;u sau đ&oacute; th&igrave; qua đời....</span></p>\r\n', 0, '2023-11-29 14:48:26', '2023-11-29 14:52:41'),
(11, 'dam-mo-lon,-dung-hoai-phi-tuoi-tre-lu-tu-hao.jpg', 'Dám Mơ Lớn, Đừng Hoài Phí Tuổi Trẻ', 'dam-mo-lon,-dung-hoai-phi-tuoi-tre', 1, 'Lư Tư Hạo', 'Thuviensach.com', 'Đang cập nhật', '<p>&nbsp;</p>\r\n\r\n<p>D&Aacute;M MƠ LỚN, ĐỪNG HO&Agrave;I PH&Iacute; TUỔI TRẺ! Những ng&agrave;y mười t&aacute;m đ&ocirc;i mươi ta đều mơ những giấc mơ lớn, đều khao kh&aacute;t đạp tung tất cả để bay ra khỏi v&ograve;ng tay của gia đ&igrave;nh, mẹ cha. Những ng&agrave;y hăm lăm nh&igrave;n lại h&igrave;nh như giấc mơ đ&atilde; ti&ecirc;u hao đi &iacute;t nhiều... Ta chậm lại, bớt k&igrave; vọng hơn, cũng bớt nỗ lực hơn. H&igrave;nh như ta kh&ocirc;ng c&ograve;n trẻ nữa. Để rồi những bộn bề của cuộc sống cuốn giấc mơ của bạn đi như d&ograve;ng nước hững hờ cuốn tr&ocirc;i chiếc l&aacute;. Cứ thế, bạn vội v&atilde; trưởng th&agrave;nh, vội v&atilde; y&ecirc;u, vội v&atilde; sống, vội v&atilde; qu&ecirc;n đi kh&aacute;t khao m&igrave;nh từng c&oacute;. Ừ th&igrave; ta lớn rồi phải kh&ocirc;ng? Nhưng bạn biết kh&ocirc;ng: Bung m&igrave;nh ra sống một đời nhiệt huyết rực rỡ hay thu m&igrave;nh lại b&igrave;nh b&igrave;nh y&ecirc;n y&ecirc;n nơi g&oacute;c nh&agrave;...lựa chọn l&agrave; ở bạn? Tuổi trẻ của bạn chẳng phải tuổi t&aacute;c hay số đếm, tuổi trẻ được định nghĩa bằng đam m&ecirc;, bằng ngọn lửa bằng khao kh&aacute;t trong bạn. Một ng&agrave;y bạn c&ograve;n sống với những kh&aacute;t vọng, c&ograve;n bản lĩnh d&aacute;m chọn những th&aacute;ch thức ch&iacute;nh l&agrave; tuổi trẻ của bạn. Trẻ m&agrave; ngu ngốc cũng được nhưng đừng bỏ cuộc. Trẻ m&agrave; sai cũng được nhưng đừng gục ng&atilde;. Trẻ m&agrave; đi chậm cũng được đi nhanh cũng được nhưng đừng qu&ecirc;n ước mơ đời m&igrave;nh. Nếu tuổi trẻ chẳng c&oacute; gian tru&acirc;n, chỉ đơn giản l&agrave; ng&agrave;y d&agrave;i th&aacute;ng rộng lẳng lặng tr&ocirc;i qua, l&agrave; sắp đặt của mẹ cha, l&agrave; an ổn sống một đời tẻ nhạt... Vậy chẳng phải ho&agrave;i ph&iacute; lắm hay sao?</p>\r\n', 0, '2023-11-29 14:56:48', '2023-11-29 15:00:11'),
(12, 'hanh-phuc-mot-minh-moc-diep-tu.jpg', 'Hạnh Phúc Một Mình', 'hanh-phuc-mot-minh', 1, 'Mộc Diệp Tử', 'Thuviensach.com', 'Đang cập nhật', '<p>&nbsp;</p>\r\n\r\n<p>C&aacute;i gi&aacute; phải trả cho trưởng th&agrave;nh l&agrave; c&ocirc; đơn.&nbsp;</p>\r\n\r\n<p><br />\r\nC&aacute;i gi&aacute; phải trả cho th&agrave;nh c&ocirc;ng l&agrave; những th&acirc;t bại.&nbsp;</p>\r\n\r\n<p><br />\r\nC&aacute;i gi&aacute; phải trả cho t&igrave;nh y&ecirc;u l&agrave; thanh xu&acirc;n kh&ocirc;ng trở lại.&nbsp;</p>\r\n\r\n<p><br />\r\nC&aacute;i gi&aacute; phải trả cho sự vĩ đại l&agrave; tr&aacute;ch nhiệm của bản th&acirc;n.&nbsp;</p>\r\n\r\n<p><br />\r\nV&agrave; c&aacute;i gi&aacute; phải trả cho t&igrave;nh th&acirc;n l&agrave; những g&igrave; c&ograve;n lại m&atilde;i m&atilde;i.</p>\r\n', 0, '2023-11-29 15:02:05', '2023-11-29 15:04:19'),
(13, 'hoang-tu-be-antoine-de-saint-expery.jpg', 'Hoàng Tử Bé', 'hoang-tu-be', 1, 'Antoine De Saint-Expéry', 'Thuviensach.com', 'Đang cập nhật', '<p>&nbsp;</p>\r\n\r\n<p><em><strong>Ho&agrave;ng Tử B&eacute; </strong></em>của nh&agrave; văn Antoine De Saint-Exp&eacute;ry đ&atilde; ra đời được gần một thế kỷ. T&aacute;c phẩm được xuất bản lần đầu ti&ecirc;n tại New York v&agrave;o năm 1943 rồi được xuất bản tại Ph&aacute;p v&agrave;o năm 1946. Đ&acirc;y ch&iacute;nh l&agrave; t&aacute;c phẩm nổi tiếng nhất của Antoine De Saint-Exp&eacute;ry v&agrave; được b&igrave;nh chọn l&agrave; t&aacute;c phẩm xuất sắc nhất thế kỉ 20 ở Ph&aacute;p. Ho&agrave;ng Tử B&eacute; kể về một cậu ho&agrave;ng tử sống tr&ecirc;n tiểu tinh cầu B612. Một ng&agrave;y nọ cậu đ&atilde; rời h&agrave;nh tinh của m&igrave;nh m&agrave; đi xem những phần c&ograve;n lại của vũ trụ. V&agrave; ở những tinh cầu kh&aacute;c cậu đ&atilde; gặp được to&agrave;n những điều kỳ quặc. Cậu bắt đầu kh&aacute;m ph&aacute; ch&uacute;ng v&agrave; nhận ra nhiều điều. Một cuốn s&aacute;ch ph&ugrave; hợp với mọi lứa tuổi.</p>\r\n', 0, '2023-11-29 15:07:03', '2023-11-29 15:10:39');

